let nav=document.querySelector('[data-js="nav"]');function toggleNav(){nav.classList.contains("open")?(document.getElementById("js-btn-mobile-toggle").classList.remove("change"),nav.classList.remove("open")):(nav.classList.add("open"),document.getElementById("js-btn-mobile-toggle").classList.add("change"))}function closeNav(){document.getElementById("js-btn-mobile-toggle").classList.remove("change"),window.screen.width<=700&&nav.removeAttribute("style")}function navButton(e){e.classList.toggle("change")}